mapper.select:
SELECT seq, i_f, f_price, f_pic, f_name
FROM t_food

    WHERE i_f = #{chk}

mapper.update:
UPDATE t_seat
SET s_no = #{s_no}
WHERE u_no = #{u_no}
  AND s_occupied = #{s_occupied}

mapper.select:
SELECT u_no, s_no, s_occupied
FROM t_seat
WHERE u_no = #{u_no}

mapper.update:
UPDATE t_user
SET
u_name = #{u_name}
, u_birth = #{u_birth}
, m_dt = now()

    , u_profile = #{u_profile}


    , salt = #{salt}
    , u_password = #{u_password}

WHERE u_no = #{u_no}

mapper.select:
SELECT r_dt, m_dt, u_totalPayment, u_totalTime
FROM t_user
WHERE u_no = #{u_no}
